function GetUniqueSegmentNum(segmentCode) {
  var SegmentsFS = GetFeatureSet($feature);
  var thisGlobalID = $feature.GlobalID;
  var existingSegment = First(Filter(SegmentsFS , "SegmentCode = @segmentCode AND GlobalID &lt;&gt; @thisGlobalID"));

  if(!IsEmpty(existingSegment )) 
     return existingSegment.UniqueSegmentNum ;
  else{
     var lastNum = Max(SegmentsFS,'UniqueSegmentNum');
     if(IsEmpty(lastNum))
         return 1
     else
         return  lastNum + 1

   }

}

